hudson.tasks.Shell.command:
#!/bin/sh
#/home/www/.profile

maxtry=5;
sleeptime=180;
logfilepath="/FILERS/cda_logs_sas1/wxs-i.ign.fr/wpgppi04s/logs/access";

DATE_1=$(echo $DATE_LAUNCH | cut -f 1 -d '_')
DATE_2=$(echo $DATE_LAUNCH | cut -f 2 -d '_' | tr '-' ':')
DATE_ALL="$DATE_1 $DATE_2"
DATE_TS=$(date -d "$DATE_ALL" "+%s")
curdate=$(($DATE_TS-15*60))
curquarter=$(($curdate - ($curdate % (15 * 60))))


result=1;
try=1;
while [ "$result" -ne "0" ] && [ "$try" -le "$maxtry" ]
do
    echo "job started - curdate="${curdate}" - curquarter="${curquarter}" - try="${try};
    java -jar /MIDDLE/gpp3/stats-integration.ign.fr/app/integration_quarter/accesscontrol-statistics-integration.jar jobContext.xml integrationJobByQuarter log.file.path=$logfilepath starting.time.job=$(date -d"@$curquarter" +\%d/\%b/\%Y\:\%H\:\%M\:\%S);
    result=$?;

    echo "job is terminated with status "${result};
    if [ "$result" -ne "0" ]
    then
        echo "An error occured during processing";
        try=$((try+1));

        if [ "$try" -le "$maxtry" ]
        then
            sleep $sleeptime;
        fi
    fi
done

exit ${result};
